def convertDatatoHistogram(ar1, ar2, bin_threshold):
    new_ar_1 = []
    new_ar_2 = []

    s = 0
    m = max(ar1)
    i = bin_threshold

    while i < m:
        new_ar_1.append(i)
        i += bin_threshold

    new_ar_1.append(i)

    for i in range(0, len(new_ar_1)):
        new_ar_2.append(None)

    k = 0
    count = 0
    for i in range(0, len(ar1)):
        while ar1[i] > new_ar_1[k]:
            if s > 0:
                new_ar_2[k] = s/float(count)
            s = 0
            count = 0
            k += 1

        if ar1[i] <= new_ar_1[k]:
            count += 1
            s += ar2[i]
        else:
            if s > 0:
                new_ar_2[k] = s/float(count)
            count = 0
            s = ar2[i]
            k += 1
    if s > 0:
        new_ar_2[k] = s/float(count)

    final_array1 = []
